custom_css:
.container {
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
}

@media (min-width: 576px) {
  .container {
    max-width: 540px;
  }
}
@media (min-width: 768px) {
  .container {
    max-width: 720px;
  }
}
@media (min-width: 992px) {
  .container {
    max-width: 960px;
  }
}
@media (min-width: 1200px) {
  .container {
    max-width: 1170px;
  }
}
.container-fluid {
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
}

@media (min-width: 1440px) {
  body:not(.w1720):not([class*="width_"]) .container-fluid {
    padding-right: 30px;
    padding-left: 30px;
  }
}

/* ---------------------------------------------
   1.5 Width
   ---------------------------------------------*/
   
.w100 {
  width: 100%;
}

.w1340 {
  max-width: 1340px;
  margin-left: auto;
  margin-right: auto;
}

@media (min-width: 1200px) {
  .width_1520 .vc_column-inner {
    max-width: 1520px;
    margin-left: auto;
    margin-right: auto;
  }

  .width_1520 .site-header .container,
  .width_1520 .container-fluid,
  .width_1520 .container {
    max-width: 1520px;
    padding-right: 15px;
    padding-left: 15px;
  }

  .width_1520 .site-header,
  .width_1520 .site-footer {
    margin-left: auto;
    margin-right: auto;
  }

  .width_1400 .vc_column-inner {
    max-width: 1400px;
    margin-left: auto;
    margin-right: auto;
  }

  .width_1400 .site-header .container,
  .width_1400 .container-fluid,
  .width_1400 .container {
    max-width: 1400px;
  }

  .width_1400 .site-header,
  .width_1400 .site-footer {
    margin-left: auto;
    margin-right: auto;
  }
}
@media (min-width: 1600px) {
  .width_1550 .vc_column-inner {
    max-width: 1550px;
    margin-left: auto;
    margin-right: auto;
  }

  .w1545 .container {
    max-width: 1545px;
  }
}
@media (min-width: 1600px) {
  .w1720 .site-header .container,
  .w1720 .container-fluid,
  .w1720 .container,.vc_row-fluid.w1720 {
    max-width: 1720px;
  }

  .w1720 .site-header,
  .w1720 .site-footer {
    margin-left: auto;
    margin-right: auto;
  }
  
  .vc_row-fluid.w1720 {
    margin-left: calc(15% - 25vw);
    margin-right: calc(15% - 25vw);
    width: auto;
    max-width: 1000%;
}
  
  
  .w1600 .site-header .container,
  .w1600 .container-fluid,
  .w1600 .container {
    max-width: 1630px;
  }

  .w1600 .site-header,
  .w1600 .site-footer {
    margin-left: auto;
    margin-right: auto;
  }
}
/************************************************/

/*Logo */

.logo {
	margin-top: 38px;
}
@media (max-width: 991px) {
.logo {
	margin-top: 23px;
}
}
@media (max-width: 576px) {
.logo {
	margin-top: 28px;
}
}
/*slider */
@media (max-width: 1200px) {
.rs_slider {
	margin-top: -45px !important;
}
}
@media (max-width: 991px) and (min-width: 575px) {
  .mobile-col-2 .vc_column_container {
    width: 50%;
    float: left;
  }
}

.fw_400{
    font-weight:400 !important;
}


/* Home1  Banner section start */

/*button style start*/
.btn_style .vc_custom_heading .block-header .banner-btn.d_btn {
	background: transparent;
	font-weight: 400;
}

.btn_style .vc_custom_heading .block-header .banner-btn.d_btn:hover {
	border: 1px solid #fff;
	background: #fff !important;
	color: #121f38 !important;
}
.container-fluid .block-header.right-center{
	padding-right: 35px;
}
.side_banner .block-wrap .block-header em{
	text-transform: uppercase;

}

@media (max-width: 1200px) {
    .side_banner .block-wrap .block-header em {
	font-size: 20px;
}
}
@media (min-width: 576px) and (max-width: 991px) {
   .side_banner .vc_col-sm-7{
    width: 100%;
} 

.side_banner .vc_col-sm-5{
	width: 100%;
} 
.banner_right .block-wrap {
	width: 50%;
	float: left !important;
}
.banner_left a img {
	max-height: 380px;
	object-fit: cover;
	object-position: center center;
}
 
}
@media (max-width: 991px) {

.banner_right .vc_custom_heading .block-header .banner-btn {
	margin-top: 15px;
}
.side_banner .container-fluid .block-header.right-center, .container-fluid .block-header.left-center{
	padding-right: 25px;
	padding-left:25px;
}
.side_banner .block-header h3{
    font-size:25px;
}
.side_banner .block-header .sub-title {
	white-space: normal;
	padding: 5px 0 6px;
}
.side_banner .block-wrap .block-header em {
	font-size: 18px;
}
.banner_right .block-wrap.right_top {
	margin-left: -15px;
	padding-left: 15px;
}
.banner_right .block-wrap.right_bottom {
	margin-left: -15px;
	padding-left: 15px;
	float: right !important;
}
.sc_title_text .vc_custom_heading{
    font-size: 23px !important;
}
.sc_title_text .sub-title {
	font-size: 14px !important;
	line-height: 24px !important;
}

}

@media (max-width: 576px) {
   .side_banner .block-header .sub-title {
	white-space: inherit;
}
.sc_title_text .vc_custom_heading{
    font-size: 30px !important;
}
}
@media (max-width: 576px) {
.slider_side_banner  .block-wrap.vc_custom_heading figure a img{
    min-height:210px;
}
}
@media (max-width: 480px) {
    .side_banner .block-header h3{
    font-size:18px;
}
.side_banner .block-header .sub-title {
	white-space: inherit;
	padding: 5px 0 4px;
}
.side_banner .block-header.left-center,.block-header.right-center{
    top:50%;
}
}

/*button style end*/

/* Home 1 Slider bottom  Banner section End */

/* Product section start */
.sc_title_text .sub-title {
	font-size: 16px;
	line-height: 26px;
}

.left_alignment .product-wrap .product-top span.product-label {
	display: none;
}

.left_alignment .product-wrap.pl-2 .product-title{
    text-transform: capitalize;
    font-size: 16px;
    font-weight: 400;
    margin-bottom: 20px;
}
.left_alignment .product-wrap.pl-2 .product-title a{
    color:#878787;
}
.left_alignment .product-wrap.pl-2 .product-price-container {
	text-align: left;
	margin: 0 32px 35px 32px;
	color: #121f38;
	font-weight: 500;
}
@media (max-width: 480px) {
.product-wrap.pl-2 .product-action {
	left: 58%;
}
}
/* Product section End */



/* CTA Banner area start */
.cta_banner .wpb_wrapper p {
	color: #f2f2f2;
}
.cta_btn .vc_btn3-style-custom {
	background: transparent !important;
	border: 1px solid #fff;
}
.cta_btn .vc_btn3.vc_btn3-style-custom:focus, .cta_btn .vc_btn3.vc_btn3-style-custom:hover {
	border: 1px solid #fff;
	background: #fff !important;
	color: #121f38 !important;
}
@media (max-width: 991px) {
.cta_banner .vc_custom_heading {
	font-size: 40px;
}
}

@media (max-width: 767px) {
    .cta_banner .vc_custom_heading {
	font-size: 30px;
}
.cta_btn .vc_btn3-style-custom {
	padding-right: 40px;
	padding-left: 40px;
}
}
@media (max-width: 575px) {
.cta_banner .vc_custom_heading {
	font-size: 25px;
}
.sc_title_text .vc_custom_heading {
	font-size: 25px !important;
}
}

/* CTA Banner area End */

/*Blog Area start */
@media (max-width: 767px) {
/*Blog Img*/
a.entry-thumbnail_overlay img {
	width: 100%;
}
}
@media (max-width: 480px) {
.sc-blog .entry.entry-grid .entry-title {
	font-size: 17px;
}
}
/*Blog Area end */

/*Footer Inner Top intro section start */
.intro_icon-block {
	display: flex;
	align-items: center;
	padding-bottom: 70px;
}

.intro_icon-wrap span {
	font-size: 45px;
	color: #ffcc33;
	margin-right: 12px;
}
.intro_icon-content h4 {
	font-size: 20px;
	font-weight: 500;
	color: #fff;
}
/*Footer Inner Top intro section end */
/*Footer area start */

.footer-inner {
	border-top: 2px solid rgba(255,255,255,0.1);
}
.footer-top{
    background-color:transparent;
}
.footer-top .widget {
	margin-bottom: 30px;
}
.footer .widget .widget-title {
	color: #fff;
	font-weight: 400;
	font-size: 20px;
}
.textwidget.custom-html-widget ul li a {
	color: #ddd;
}

.textwidget.custom-html-widget ul li:hover a {
	color: #ffcc33;
}
.textwidget.custom-html-widget p {
	color: #ddd;
	margin-bottom: 30px;
}

.d_btn a {
	background: #fff;
	padding: 15px 17px;
	display: inline-block;
	color: #121f38;
	font-size: 16px;
	font-weight: 400;
	float: left;
	border-radius: 2px;
	transition: 0.3s;
	margin-right: 5px;
	min-width: 165px;
	margin-bottom: 10px;
}
.d_btn a i {
	font-size: 25px;
	float: left;
	margin-right: 8px;
}
.d_btn:hover >a{
    background-color: #ffcc33;
    color:#fff;
}

.d_btn2 a {
	background: #fff;
	padding: 15px 17px;
	display: inline-block;
	color: #121f38;
	font-size: 16px;
	font-weight: 400;
	float: left;
	border-radius: 2px;
	transition: 0.3s;
	min-width: 165px;
}
.d_btn2 a i {
	font-size: 25px;
	float: left;
	margin-right: 8px;
}
.d_btn2:hover >a{
    background-color: #ffcc33;
    color:#fff;
}

.footer.has-bg-image .footer-bottom {
	border-top: 1px solid #313a4d;
}

/*Footer*/
@media (max-width: 1200px) {
    .footer-bottom.footer-layout-4 .col-sm-4{
        width:50%;
        float:left;
    }
    .footer-bottom .payments-icon {
	text-align: left;
    }
    .footer-bottom .footer-link-menu {
	text-align: left;
    }
}
@media (max-width: 991px) {
    .footer-bottom.footer-layout-4 .col-sm-4{
        width:100%;
    }
}
/*Home One Responsive*/

/*footre*/
.intro_icon-block {
	padding-bottom: 0px;
}
.footer.has-bg-image {
	background-position: center center !important;
	background-size: cover !important;
}

}
/*Home 2 page start */


/* Icon block*/
.icon-layout-1 .icon-content h4 {
	margin-bottom: 10px;
}

@media (min-width: 576px) and (max-width: 991px) {
  .mobile-col-2 .vc_column_container {
    width: 50%;
    float: left;
  }
}




/*Best Selling*/
.best_sell .widget ul li {
	margin-bottom: 15px;
}


/*Testimonial Section */

.sc-tl-3 .testimonial blockquote {
	background: #fff;
	padding: 20px 30px;
	box-shadow: 0px 11px 35px 0px rgba(0, 0, 0, 0.04);
}
.piko-carousel .testimonial {
	margin-top: 0px;
}

/*footer */
.footer-top #custom_html-10.widget {
	margin-bottom: 70px;
}
/* Home 3 area*/
@media (max-width: 991px) {
    .slide_with_vartical_menu .vc_col-sm-9 {
    width: 100%;
    }
}


/*mega menu*/
.category-menu .secondary-menu-wrapper .secondary-title {
	color: #fff !important;
	border-right-color: transparent !important;
	border-left-color: transparent !important;
}
.mega-shortcode .secondary-menu-wrapper .secondary-title {
	background-color: #121f38;
}
.category-menu.mega-shortcode ul li {
	padding: 0;
}

/*Icon Block */
.icon_block_title .icon-block .icon-content {
	vertical-align: middle;
}

/*Offer Coundown section*/
.offer_countdown {
	padding: 60px 50px;
	background: #fff;
	border: 1px solid #ddd;
}
.offer_countdown .vc_custom_heading {
	font-size: 30px;
	font-weight: 500;
	color: #121f38;
	margin: 0;
}
.offer_countdown .wpb_content_element {
	margin: 0 0 15px;
}
.offer_countdown .wpb_text_column p {
	font-size: 16px;
	color: #787878;
	margin: 17px 0 20px;
}
.offer_countdown .wpb_raw_code del {
	opacity: 0.8;
	font-size: 16px;
}

.offer_countdown .wpb_raw_code .price {
	font-size: 26px;
	color: #121f38;
	font-weight: 500;
}

.offer_countdown .piko_countdown-1 .countdown-amount {
	font-size: 18px;
	display: block;
}

.offer_countdown  .piko_countdown-1 .countdown-period {
	font-size: 16px;
}

.offer_countdown .piko_countdown-1 .countdown-section {
	padding: 10px 24px;
	color: #222;
	display: inline-block;
	border: 1px solid #ddd;
	margin-right: 10px;
	text-align: center;
}

.count_add_btn {
	margin-top: 35px;
}
.offer_countdown  .count_add_btn a.cart_btn {
	background: #ffcc33;
	color: #fff;
	display: inline-block;
	padding: 14px 35px;
	outline: none;
	font-size: 14px;
	line-height: 1.2;
	margin-right:5px;
}
.count_add_btn a.wish_btn {
	background: #121f38;
	color: #ffff;
	font-size: 14px;
	padding: 13px 17px;
	line-height: 1px;
	outline: none;
}

@media (max-width: 991px) {
   .offer_section .vc_col-sm-9 {
       width:100%;
   }
     .offer_section .vc_col-sm-3 {
       width:100%;
   }
   .offer_countdown {
       margin-bottom:60px;
   }
   
}

@media (min-width: 992px) and (max-width: 1200px) {
    .offer_countdown .vc_custom_heading {
	font-size: 25px;
}
.offer_countdown .piko_countdown-1 .countdown-section {
	padding: 8px 20px;
	margin-right: 4px;
}
}
@media (min-width: 768px) and (max-width: 991px) {
    .offer_countdown {
	padding: 48px 20px;
}
.offer_countdown .vc_custom_heading {
	font-size: 24px;
}
.offer_countdown .wpb_raw_code .price {
	font-size: 23px;
}
.offer_countdown .piko_countdown-1 .countdown-section {
	padding: 9px 22px;
}
}
@media (max-width: 576px) {
.offer_countdown {
	padding: 40px 20px;
}
}
@media (max-width: 480px) {
.offer_countdown {
	padding: 40px 10px;
}
    .offer_countdown .vc_custom_heading {
	font-size: 19px;
}
.offer_countdown .wpb_text_column p {
	font-size: 14px;
}
.offer_countdown .wpb_raw_code .price {
	font-size: 20px;
}
.offer_countdown .piko_countdown-1 .countdown-section {
	padding: 0px 10px;
	margin-right: 5px;
}
.best_selling_widgets .wpb_wrapper {
	padding-bottom: 0;
}
.best_selling_widgets .widget ul li {
	margin-bottom: 10px;
}


}
/*best selling*/
.best_selling_widgets .al .slick-arrow {
	opacity: 1;
}
.best_selling_widgets .stcr .slick-arrow {
	width: 25px;
	height: 25px;
	line-height: 24px;
	color: #fff;
	top: -30px;
	margin-right: 10px;
}

.best_selling_widgets .wpb_wrapper{
	background: #fff;
	border: 1px solid #ddd;
	padding-bottom: 35px;
}
@media (min-width: 320px) and (max-width: 992px) {
    .best_selling_widgets .wpb_wrapper{
	padding-bottom: 0px;
}
}
.best_selling_widgets h4.widget-title {
	background: #121f38;
	color: #fff;
	font-weight: 400;
	padding: 20px 30px;
	position: relative;
	margin:0px;
}
.best_selling_widgets .stcr.slick-initialized {
	padding-top: 35px;
}
.best_selling_widgets .slick-track ul{
	padding-right: 20px;

}
.best_selling_widgets .widget ul li {
	margin-bottom: 20px;
}


/* offer Banner */
@media (min-width: 768px) and (max-width: 1199px) {
.offer_banner .block-wrap.vc_custom_heading figure a img {
	min-height: 250px !important;
	object-fit: cover;
	object-position: center center;
}
}
@media  (max-width: 575px) {
.offer_banner .block-wrap.vc_custom_heading figure a img {
	min-height: 200px !important;
	object-fit: cover;
	object-position: center center;
}
    
}


/* Brand Logo */
.Brand_logo .sc-brand figure img {
	height: auto;
	width: 70%;
}
/*Subcribe Form*/
.subcribe_form form input[type="email"] {
	border: none;
	border-bottom: 1px solid #eee;
	position: relative;
}

.subcribe_form form input[type="submit"] {
	position: absolute;
	top: 13px;
	right: 0;
	border: none;
	color: #121f38;
	font-size: 16px;
	font-weight: 400;
}
.subcribe_form form input[type="submit"]:hover{
    background: transparent;
    color: #fc3 !important;
}

.border_top {
	border-top: 1px solid #ddd;
}


/*About Us Page */

/* Brand*/
.footer-top .sc-brand figure img {
	width: 75%;
}

/*product list widgets*/

.product_list .widget-title {
	margin-bottom: 35px;
	font-weight: 500;
	color: #121f38;
}

@media  (max-width: 991px) {
.about_content .vc_column-inner {
	padding-left: 15px !important;
}
.mission_content .vc_column-inner {
	padding-right: 15px !important;
}
}
