/*Footer Section CSS*/
.footer_logo {
	width: 110px;
	margin-bottom: 10px;
}
.footer .widget .widget-title {
	font-size: 18px;
	color: #22222b;
	font-weight: 400;
}
.footer-inner {
	padding: 90px 0 35px;
}
.textwidget p a {
	border-bottom:1px solid #eee;
}
.textwidget p a:hover {
	color:#f73151;
	border-color:#f73151;
}
.textwidget p span {
	display: block;
}
.textwidget ul.links li a{
    position:relative;
}
.textwidget ul.links li a::before {
	opacity: 0;
	visibility: hidden;
	background: #f73151;
	height: 1px;
	width: 100%;
	position: absolute;
	content: "";
	left: 0;
	bottom: 0;
}

.textwidget ul.links li:hover a:before {
	color: #f73151;
	opacity: 1;
	visibility: visible;
}
.textwidget ul.links li:hover a{
	color: #f73151;
}

.footer .footer-bottom a:hover{
	color: #f73151 !important;
}
