custom_css:
/*-------------------------------------
        Container Width spreed
-------------------------------------*/
.container {
  width: 100%;
  padding-right: 0.9375rem;
  padding-left: 0.9375rem;
  margin-right: auto;
  margin-left: auto;
}

@media (min-width: 576px) {
  .container {
    max-width: 33.75rem;
  }
}
@media (min-width: 768px) {
  .container {
    max-width: 45rem;
  }
}
@media (min-width: 992px) {
  .container {
    max-width: 60rem;
  }
}
@media (min-width: 1200px) {
  .container {
    max-width: 73.125rem;
  }
}
.container-fluid {
  width: 100%;
  padding-right: 0.9375rem;
  padding-left: 0.9375rem;
  margin-right: auto;
  margin-left: auto;
}

@media (min-width: 1440px) {
  body:not(.w1720):not([class*="width_"]) .container-fluid {
    padding-right: 1.875rem;
    padding-left: 1.875rem;
  }
}

/* ---------------------------------------------
   1.5 Width
   ---------------------------------------------*/
.w100 {
  width: 100%;
}

.w1340 {
      max-width: 83.75rem;
      margin-left: auto;
      margin-right: auto;
}

@media (min-width: 1200px) {
  .width_1520 .vc_column-inner {
        max-width: 95rem;
        margin-left: auto;
        margin-right: auto;
  }

  .width_1520 .site-header .container,
  .width_1520 .container-fluid,
  .width_1520 .container {
        max-width: 95rem;
        padding-right: 1.875rem;
        padding-left: 1.875rem;
  }

  .width_1520 .site-header,
  .width_1520 .site-footer {
        margin-left: auto;
        margin-right: auto;
  }

  .width_1400 .vc_column-inner {
        max-width: 87.5rem;
        margin-left: auto;
        margin-right: auto;
  }

  .width_1400 .site-header .container,
  .width_1400 .container-fluid,
  .width_1400 .container {
        max-width: 87.5rem;
  }

  .width_1400 .site-header,
  .width_1400 .site-footer {
        margin-left: auto;
        margin-right: auto;
  }
}

@media (min-width: 1600px) {
  .width_1550 .vc_column-inner {
    max-width: 96.875rem;
    margin-left: auto;
    margin-right: auto;
  }

  .w1545 .container {
        max-width: 96.5625rem;
  }
}

@media (min-width: 1600px) {
  .w1720 .site-header .container,
  .w1720 .container-fluid,
  .w1720 .container,.vc_row-fluid.w1720 {
        max-width: 107.5rem;
  }

  .w1720 .site-header,
  .w1720 .site-footer {
      margin-left: auto;
      margin-right: auto;
  }
  
  .vc_row-fluid.w1720 {
        margin-left: calc(15% - 25vw);
        margin-right: calc(15% - 25vw);
        width: auto;
        max-width: 1000%;
    }
  
  .w1600 .site-header .container,
  .w1600 .container-fluid,
  .w1600 .container {
    max-width: 102rem;
  }

  .w1600 .site-header,
  .w1600 .site-footer {
        margin-left: auto;
        margin-right: auto;
  }
  
  .w1300 .site-header .container,
  .w1300 .container-fluid,
  .w1300 .container {
    max-width: 81.25rem;
  }

  .w1300 .site-header,
  .w1300 .site-footer {
        margin-left: auto;
        margin-right: auto;
  }
}
/*---------------------------
        H1 Banner
-----------------------------*/
.H1_Wedding_banner .block-header.left-top {
	top: 25px;
	left: 15px;
}

/*----------------------------
    H1 Product
------------------------------*/
@media (min-width: 321px) and (max-width: 767px) {
.h1_wedding_product .product-countdown .countdown-amount {
	font: 400 16px/1 sans-serif !important;
}
.h1_wedding_product .product-countdown .countdown-period {
	font-size: 9px !important;

}
.h1_wedding_product .product-top .product-countdown .countdown-section {
	padding: 15px;
}
}

/*---------------------------
    H1 Brand Logo
-----------------------------*/
.h1_wedding_sep.wpb_content_element{
    margin-bottom:0 !important;
}


/*---------------------------
    H2 Icon Block
-----------------------------*/
.wpb_content_element.h2_wedding_sep {
	margin-bottom: 0;
}
@media (min-width: 576px) and (max-width: 991px) {
.h2_wedding_iconblock .vc_col-sm-3 {
	width: 50%;
	float: left;
}
}

/* H3 Video */

@media (max-width: 991px){ 
.h3_video .video-btn._2x {
	width: 100px;
	height: 100px;
	line-height: 100px;
	font-size: 2rem;
}
}
/*H3 countdown or funfact */
.border_top_bottom{
    border-top: 1px solid #ebebeb;
    border-bottom: 1px solid #ebebeb;
}
.h2_countdown .block_desc p {
	margin: 0;
}
.icon-layout-3 .icon_count {
	line-height: 1 !important;
}
@media (min-width: 576px) and (max-width: 991px) {
.h2_countdown .vc_col-sm-3 {
	width: 50%;
	float: left;
}
}

/* H3 Testimonial */
.h3_testimonial .testimonial blockquote {
	padding: 30px 50px 50px;
}


/* H3 Priceing */
.h3_priceing_border{
    border-bottom: 1px solid #ebebeb;
}
/*-----------------------------
            Width End
------------------------------*/

.site-header .header-main {
    border-bottom: none !important;
}

.btn-style .block-header .banner-btn {
	font-weight: 400;
	background: #ffecec;
	border: none;
}


/*Newslatter css*/
.signup-newsletter h3 {
	font-size: 36px;
	font-family: Playfair Display;
}
.signup-newsletter .desc {
	max-width: 550px;
	margin-bottom: 30px;
	font-size: 16px;
}
.signup-newsletter-form {
	border: none;
	border-bottom: 1px solid #ddd;
}
.signup-newsletter-form .submit-btn {
	border: none;
	font-size: 16px !important;
	padding: 0 24px;
	color: #141414;
	background-color: transparent !important;
	text-transform: capitalize;
	font-weight: 400;
	right: -30px;
}
input[type="submit"]:hover, button:hover, a.button:hover, .piko-ajax-load a:hover {
	color: #000 !important;
}
.signup-newsletter:not(.newsv3) .banner-content {
	width: 70% !important;
	background-color: #fff;
	padding: 150px 0;
	margin: 0 auto;
}

.signup-newsletter:not(.newsv3) .banner-content::before {
	position: absolute;
	content: "";
	left: 2%;
	top: 5%;
	height: 90%;
	width: 96%;
	border: 10px solid transparent;
	-webkit-border-image: url(http://localhost/weddingStock/wp-content/uploads/2019/07/newslatter-bg-h2.jpg) 30 stretch;
	border-image: url(http://localhost/weddingStock/wp-content/uploads/2019/07/newslatter-bg-h2.jpg) 30 stretch;
	padding: 10px;
}

/*End newslatter*/

/*Icon */
.icon-block .icon-content {
	vertical-align: middle !important;
}
.icon-layout-2.text-left.tac {
	text-align: center;
}

/*video btn*/
.video-btn {
    background-color: transparent;
	border: 1px solid #fff;
}

.video-btn._2x {
	width: 150px;
	height: 150px;
	line-height: 150px;
}

/*testimonial home 3 css*/
.testimonial figure {
	border: none;
	background-color: transparent;
}
.testimonial blockquote {
	padding: 50px 50px;
	font-size: 16px;
	border: none;
	max-width: 1050px;
	margin: 0 auto;
	text-align: center;
}



/*blog css */
.sc-bl-1 .entry.entry-grid .entry-content-wrapper {
	padding: 5px 5px 0 0;
	text-align: center;
}

/*product category thumbnail */
.brand-cat-meta {
	width: 80%;
	text-align: left;
	position: absolute;
	left: 50px;
	bottom: 0;
	background-color: #fff;
	padding: 20px;
    -webkit-transition: all .5s;
    transition: all .5s;
    visibility: hidden;
    opacity: 0;
}

.brand-cat-meta h4 {
	font-size: 30px;
}

.brand-cat.slick-slide:hover .brand-cat-meta {
    left: 0;
    visibility: visible;
    opacity: 1;
}

/*product countdown*/

.product-top .product-countdown .countdown-section {
  background-color: #ffbddf;
  padding: 15px
}

.product-top .product-countdown span {
  color: #fff;
}

/*widget area */

.textwidget p > img {
	height: auto;
	max-width: calc(50% - 20px);
	vertical-align: middle;
}

/*hero slider*/
.custom .tp-bullet {
	width: 30px;
	height: 4px;
}

.custom .tp-bullet.selected {
	background: #121f38;
	border-color: #121f38;
}

/*Responsive css */

@media (max-width: 767px) {
    .block-box a img {
    	object-fit: cover;
    	min-height: 350px;
    }
    
    .signup-newsletter figure img {
    	min-height: 350px;
    	object-fit: cover;
    }
    
    .footer-bottom .payments-icon {
	    text-align: left;
	    margin: 10px 0;
    }
    
    .footer-bottom .footer-link-menu {
	    text-align: left;
    }
    .signup-newsletter {
	    display: none;
    }
}

@media (max-width: 1199px) {

    .footer-bottom .payment-info, .footer-link-menu {
    	line-height: 26px;
    }
}

/* Footer */
.footer .widget .widget-title {
	font-size: 20px;
	letter-spacing: 0.07em;
	margin: -2px 0 20px;
	color: #121f38;
	font-weight: 400;
}
.widget.widget_nav_menu ul li {
	margin-bottom: 10px;
}
.footer a {
	color: #818491;
}
.footer {
	background-color: transparent !important;
}
.footer-bottom.footer-layout-4 {
	border-top: 1px solid #ebebeb;
}
